<script>
import { mapActions, mapGetters } from "vuex";
export default {
  name: "app-navbar",
  data: () => ({}),
  methods: {
    onLogout() {
      this.$store.dispatch("logoutUser");
      this.$router.push("/");
    },
    ...mapActions(["setShowCart", "setShowDialog"]),
    showCart() {
      this.setShowCart();
      document.querySelector(".cart").classList.add("active-cart");
    },
    openModalSettings() {
      this.setShowDialog();
    },
  },
  computed: {
    ...mapGetters(["cartProducts"]),
    isUserLoggedIn() {
      return this.$store.getters.isUserLoggedIn;
    },
  },
};
</script>
